import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getConference, getMembers } from '../../services/api';
import MemberCard from '../../components/MemberCard/MemberCard';

const ConferencePage = () => {
  const { id } = useParams();
  const [conference, setConference] = useState(null);
  const [members, setMembers] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const conferenceData = await getConference(id);
        const membersData = await getMembers(id);
        setConference(conferenceData.find((event) => event.id === +id));
        setMembers(membersData);
      } catch (error) {
        console.error('Ошибка загрузки данных:', error);
      }
    };
    fetchData();
  }, [id]);

  return (
    <div>
      <h1>{conference?.event_name || 'Загрузка...'}</h1>
      <div>
        {members.map((member) => (
          <MemberCard key={member.id} member={member} />
        ))}
      </div>
    </div>
  );
};

export default ConferencePage;
