import React, { useState, useEffect } from 'react';
import ReactionSelector from '../ReactionSelector/ReactionSelector';
import './MemberCard.scss';

const MemberCard = ({ member }) => {
  const [reaction, setReaction] = useState(null);
  const [ifVoted, setIfVoted] = useState(false); // Локальная переменная для отслеживания голосования

  // Загружаем реакцию из localStorage и состояние голосования при монтировании компонента
  useEffect(() => {
    const savedReaction = localStorage.getItem(`reaction-${member.id}`); // Получаем реакцию из localStorage
    const savedVotedStatus = localStorage.getItem(`voted-${member.id}`); // Проверяем, проголосовал ли пользователь

    if (savedReaction) {
      setReaction(savedReaction); // Если есть сохраненная реакция, устанавливаем её
    }

    if (savedVotedStatus === 'true') {
      setIfVoted(true); // Если пользователь уже проголосовал, обновляем состояние
    }
  }, [member.id]);

  // Обработчик выбора реакции
  const handleReactionSelect = (emoji) => {
    if (!ifVoted) {  // Если еще не проголосовано
      setReaction(emoji);
      localStorage.setItem(`reaction-${member.id}`, emoji); // Сохраняем реакцию в localStorage
    }
  };

  // Обработчик подтверждения голосования
  const handleConfirmClick = () => {
    if (!ifVoted) {
      // Сохраняем информацию о голосовании
      localStorage.setItem(`voted-${member.id}`, 'true');  // Устанавливаем флаг, что пользователь проголосовал
      setIfVoted(true); // Обновляем локальное состояние
      // Здесь можно добавить код для отправки реакции на сервер
      console.log('Голос отправлен за участника', member.id, 'с реакцией:', reaction);
    }
  };

  return (
    <div className="member-card">
      <h3>{member.member_name}</h3>
      <p>{member.speech_theme}</p>

      {ifVoted ? (
        <p>Ваш голос за участника уже принят!</p>
      ) : (
        <ReactionSelector selected={reaction} onSelect={handleReactionSelect} />
      )}

      <button
        className="confirm-button"
        onClick={handleConfirmClick}
        disabled={ifVoted}  // Кнопка "Подтвердить" должна быть заблокирована, если уже проголосовано
      >
        {ifVoted ? 'Голос учтен' : 'Подтвердить'}
      </button>
    </div>
  );
};

export default MemberCard;
