import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useParams } from 'react-router-dom';
import { fetchMembers } from '../../store/slices/membersSlice';
import { fetchReactionCounts } from '../../store/slices/resultsSlice';
import './ResultsPage.scss';

const emojiMap = {
  1: 'üëç',
  2: 'üëè',
  3: '‚ù§Ô∏è',
  4: 'üéâ',
};

const ResultsPage = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const { members } = useSelector((state) => state.members);
  const resultsState = useSelector((state) => state.results || {}); 
  const results = resultsState.results?.[id] || []; // –ü—Ä–µ–¥–æ—Ç–≤—Ä–∞—â–∞–µ–º undefined

  const [loadingResults, setLoadingResults] = useState(true);

  useEffect(() => {
    dispatch(fetchMembers(id));
  }, [id, dispatch]);

  useEffect(() => {
    if (members.length > 0) {
      Promise.all(members.map(member => dispatch(fetchReactionCounts(member.id))))
        .then(() => setLoadingResults(false));
    }
  }, [members, dispatch]);

  if (loadingResults) return <div>–ó–∞–≥—Ä—É–∑–∫–∞ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤...</div>;

  return (
    <div className="results-container">
      <h1>–†–µ–∑—É–ª—å—Ç–∞—Ç—ã –≥–æ–ª–æ—Å–æ–≤–∞–Ω–∏—è</h1>
      {members.map((member) => (
        <div key={member.id} className="member-results">
          <h2>{member.member_name}</h2>
          <p>{member.speech_theme}</p>
          <div className="reaction-results">
            {results[member.id]?.map(({ reaction_id, r_count }) => (
              <div key={reaction_id} className="reaction-item">
                {emojiMap[reaction_id]} {r_count}
              </div>
            )) || <p>–ù–µ—Ç –≥–æ–ª–æ—Å–æ–≤</p>}
          </div>
        </div>
      ))}
    </div>
  );
};

export default ResultsPage;
