// src/pages/ConferencePage/ConferencePage.jsx
import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useParams } from 'react-router-dom';
import { fetchMembers } from '../../store/slices/membersSlice';
import { sendVotes } from '../../store/slices/votingSlice';  // Импортируем action для отправки голосов
import MemberCard from '../../components/MemberCard/MemberCard';
import './ConferencePage.scss';

const ConferencePage = () => {
  const { id } = useParams(); // Извлекаем id из URL
  const dispatch = useDispatch();
  const { members, status, error } = useSelector((state) => state.members);
  const votingStatus = useSelector((state) => state.voting.status); // Статус отправки голосов
  const [reactions, setReactions] = useState({}); // Объект для хранения всех реакций

  useEffect(() => {
    dispatch(fetchMembers(id)); // Загружаем участников конференции
  }, [id, dispatch]);

  const handleReactionSelect = (memberId, reaction) => {
    setReactions((prevReactions) => ({
      ...prevReactions,
      [memberId]: reaction, // Сохраняем реакцию для каждого участника
    }));
  };

  const handleConfirmClick = () => {
    // Подготовка данных для отправки на сервер
    const reactionsData = Object.keys(reactions).map((memberId) => ({
      member_id: parseInt(memberId), // Преобразуем id участника в число
      reaction_id: reactions[memberId], // Реакция участника
      event_id: id, // id события (например, конференции)
    }));

    // Отправляем данные на сервер через Redux
    dispatch(sendVotes(reactionsData));
  };

  if (status === 'loading') {
    return <div>Загрузка участников...</div>;
  }

  if (status === 'failed') {
    return <div>Ошибка: {error}</div>;
  }

  return (
    <div className="conference-container">
      <h1>Участники конференции</h1>
      <div>
        {members.map((member) => (
          <MemberCard
            key={member.id}
            member={member}
            onReactionSelect={handleReactionSelect} // Передаем обработчик в MemberCard
          />
        ))}
      </div>

      {/* Кнопка Подтвердить */}
      <button
        className="confirm-button"
        onClick={handleConfirmClick}
        disabled={votingStatus === 'loading'} // Дизейблим кнопку, если уже идет отправка
      >
        Подтвердить
      </button>

      {/* Сообщение об ошибке или успешной отправке */}
      {votingStatus === 'failed' && <div>Ошибка при отправке голосов: {error}</div>}
      {votingStatus === 'succeeded' && <div>Голоса успешно отправлены!</div>}
    </div>
  );
};

export default ConferencePage;
