import axios from 'axios';

const API_BASE = 'https://api.timetocode.ru/api';

// Получить данные о конференции
export const getConference = (eventId) =>
  axios.get(`${API_BASE}/vote-events`).then((res) => res.data);

// Получить участников конференции
export const getMembers = (eventId) =>
  axios.get(`${API_BASE}/members/${eventId}`).then((res) => res.data);

// Отправить голос
export const voteForMember = (voteData) =>
  axios.post(`${API_BASE}/vote-for-member`, voteData).then((res) => res.data);
