import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { fetchReactionCounts } from '../../store/slices/resultsSlice';

const ResultsPage = () => {
  const { id } = useParams();
  const dispatch = useDispatch();

  // Проверяем, есть ли state.results
  const resultsState = useSelector((state) => state.results || {}); 
  const results = resultsState.results?.[id] || []; // Предотвращаем undefined

  useEffect(() => {
    if (id) {
      dispatch(fetchReactionCounts(id));
    }
  }, [id, dispatch]);

  if (!results.length) {
    return <div>Загрузка результатов...</div>;
  }

  return (
    <div>
      <h1>Результаты голосования</h1>
      <ul>
        {results.map((reaction) => (
          <li key={reaction.reaction_id}>
            Реакция {reaction.reaction_id}: {reaction.r_count} голосов
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ResultsPage;
