import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useParams, useNavigate } from 'react-router-dom';
import { fetchMembers } from '../../store/slices/membersSlice';
import MemberCard from '../../components/MemberCard/MemberCard';
import { sendVote } from '../../store/slices/votingSlice';
import './ConferencePage.scss';

const ConferencePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { members, status, error } = useSelector((state) => state.members);

  const [votesCount, setVotesCount] = useState(0);
  const [showResultsButton, setShowResultsButton] = useState(false);

  useEffect(() => {
    dispatch(fetchMembers(id));
  }, [id, dispatch]);

  useEffect(() => {
    const count = members.filter(member => localStorage.getItem(`voted-${member.id}`) === 'true').length;
    setVotesCount(count);
    setShowResultsButton(count === members.length && members.length > 0);
  }, [members]);

  if (status === 'loading') return <div>–ó–∞–≥—Ä—É–∑–∫–∞ —É—á–∞—Å—Ç–Ω–∏–∫–æ–≤...</div>;
  if (status === 'failed') return <div>–û—à–∏–±–∫–∞: {error}</div>;

  const handleVote = (memberId, reaction) => {
    const voteData = {
      event_id: parseInt(id),
      member_id: parseInt(memberId),
      reaction_id: reaction,
    };

    dispatch(sendVote(voteData));

    if (!localStorage.getItem(`voted-${memberId}`)) {
      localStorage.setItem(`voted-${memberId}`, 'true');
      setVotesCount(prev => prev + 1);
    }

    setShowResultsButton(votesCount + 1 === members.length);
  };

  return (
    <div className="conference-container">
      <h1>–£—á–∞—Å—Ç–Ω–∏–∫–∏ –∫–æ–Ω—Ñ–µ—Ä–µ–Ω—Ü–∏–∏</h1>

      <p className="motivation-text">
        üåü –ú—ã –∑–∞ –¥–æ–±—Ä–æ –∏ –ø–æ–∑–∏—Ç–∏–≤! –ù–µ –∑–∞–±—É–¥—å—Ç–µ –ø–æ—Å—Ç–∞–≤–∏—Ç—å —Ä–µ–∞–∫—Ü–∏—é –∫–∞–∂–¥–æ–º—É —É—á–∞—Å—Ç–Ω–∏–∫—É, –≤–µ–¥—å –∫–∞–∂–¥—ã–π –¥–æ—Å—Ç–æ–∏–Ω –≤–Ω–∏–º–∞–Ω–∏—è. üíõ
      </p>

      <div className="vote-progress">{votesCount}/{members.length}</div>

      <div>
        {members.map((member) => (
          <MemberCard key={member.id} member={member} onVoteComplete={handleVote} />
        ))}
      </div>

      {showResultsButton && (
        <button className="results-button" onClick={() => navigate(`/results/${id}`)}>
          –ü–µ—Ä–µ–π—Ç–∏ –∫ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞–º
        </button>
      )}
    </div>
  );
};

export default ConferencePage;
